CREATE ALGORITHM = MERGE VIEW mmr_graphs AS SELECT *, cf*envelope/population*100000 as rate, cf*envelope as deaths FROM cod_studies LEFT JOIN cod_observations USING (study_id) LEFT JOIN cause_fractions USING (observation_id) LEFT JOIN population USING (iso3,year,sex,age) LEFT JOIN mortality_envelope USING (iso3,year,sex,age) LEFT JOIN fertility USING (iso3,year) LEFT JOIN sexes USING (sex) LEFT JOIN ages USING (age) LEFT JOIN countries USING (iso3) WHERE age>14 AND age<50 AND sex=2 AND cause="A10";